mapper.select:
/*selectComThemeCodeList*/ SELECT THEME_ID, THEME_NAME, THEME_LEVEL, UP_THEME_ID, THEME_DESC, THEME_CODE, NVL(THEME_ORDR,1) AS THEME_ORDR FROM STOCK_THEME_CODE WHERE 1=1
AND THEME_NAME LIKE '%' || #{searchKeyword} || '%'
ORDER BY NVL(THEME_ORDR,1) ASC , THEME_ID ASC

mapper.update:
	/*updateComThemeCode*/
    UPDATE STOCK_THEME_CODE	
    SET	THEME_NAME = #{themeName},
			THEME_LEVEL = #{themeLevel},
			UP_THEME_ID = #{upThemeId},
			THEME_DESC = #{themeDesc},
			THEME_CODE = #{themeCode},
			THEME_ORDR = #{themeOrdr}
    WHERE THEME_ID = #{themeId}
