SELECT /*selectDartList*/ A.API_GRP_CD ,A.API_GRP_NUM ,A.API_GRP_NM ,A.API_NUM ,A.API_NM ,A.API_ID ,A.DETAIL ,A.URL_GUBUN ,A.MATHOD ,A.ENCODING ,A.JSON_YN ,A.XML_YN ,A.ZIP_YN ,A.RESULT_TYPE ,REG_DATE FROM STOCKS_DART_API_LIST A WHERE 1=1
AND A.API_GRP_CD = #{apiGrpCd}
AND A.API_ID = #{apiId}
ORDER BY A.API_GRP_NUM , A.API_NUM
