mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT /*selectStockResearchDataList*/ A.RP_ID, A.ORIGIN_UID, A.RP_TITLE, TO_CHAR(TO_DATE(A.RP_DATE) , 'YYYY-MM-DD') AS RP_DATE, A.RP_LINK, A.REL_STOCK_CODE, B.STOCKS_NAME, A.STOCK_RECOMMENDATION, (SELECT CODE_NM FROM COMTCCMMNDETAILCODE WHERE USE_AT='Y' AND CODE_ID='STRE' AND CODE=A.STOCK_RECOMMENDATION) AS STOCK_RECOMMENDATION_NM, NVL(A.DAY_PRICE, 0) AS DAY_PRICE, NVL(A.TARGET_PRICE, 0) AS TARGET_PRICE, DBMS_LOB.SUBSTR(A.USER_SUMMARY, 100, 1) AS USER_SUMMARY, A.WHEN_REBOUND, A.RP_GUBUN, A.RP_CL, TO_CHAR(A.UPT_DATE , 'YYYY-MM-DD') AS UPT_DATE, TO_CHAR(A.REG_DATE , 'YYYY-MM-DD') AS REG_DATE FROM STOCK_RESEARCH_DATA A, STOCKS_LIST B WHERE A.REL_STOCK_CODE = B.STOCKS_CODE(+) AND A.RP_GUBUN = NVL(#{searchGubun},'company')
AND A.STOCK_RECOMMENDATION = #{searchStockRecommendation}
AND A.RP_TITLE LIKE '%'||#{keyword}||'%'
AND A.RP_DATE BETWEEN #{writeFromDate} AND #{writeToDate}
AND A.REL_STOCK_CODE = #{itemCode}
ORDER BY A.RP_DATE DESC , A.UPT_DATE DESC ) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}


SELECT 
	COUNT(A.RP_ID) AS CNT
FROM STOCK_RESEARCH_DATA A,
		STOCKS_LIST B
WHERE A.REL_STOCK_CODE =  B.STOCKS_CODE(+)
AND A.RP_GUBUN = NVL(#{searchGubun},'company')


		AND A.STOCK_RECOMMENDATION = #{searchStockRecommendation}



			AND A.RP_TITLE LIKE '%'||#{keyword}||'%'




			AND A.RP_DATE BETWEEN #{writeFromDate}  AND #{writeToDate} 




			AND A.REL_STOCK_CODE = #{itemCode}
